contracts/AntiFraud.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AntiFraud
 * @notice Implements anti-fraud mechanisms including rate limiting, stake forfeiture, and duplicate detection
 */
contract AntiFraud is Ownable {
    // Structs
    struct SubmissionRecord {
        uint256 timestamp;
        string imageHash;
        bytes32 metadataHash;
    }

    struct DailySubmissionCount {
        uint256 date; // Day timestamp (rounded to start of day)
        uint256 count;
    }

    // Constants
    uint256 public constant MAX_SUBMISSIONS_PER_DAY = 20;
    uint256 public constant DUPLICATE_SIMILARITY_THRESHOLD = 95; // 95%
    uint256 public constant ONE_DAY = 1 days;

    // State variables
    mapping(address => DailySubmissionCount) public workerDailySubmissions;
    mapping(address => SubmissionRecord[]) public workerSubmissions;
    mapping(string => bool) public usedImageHashes;
    mapping(string => address) public imageHashToWorker;
    mapping(address => uint256) public workerStakes;
    mapping(address => bool) public blacklistedWorkers;

    address public verificationContractAddress;
    address public taskManagerAddress;

    // Events
    event SubmissionRecorded(
        address indexed worker,
        uint256 indexed taskId,
        string imageHash,
        uint256 timestamp
    );

    event RateLimitExceeded(
        address indexed worker,
        uint256 currentCount,
        uint256 maxAllowed
    );

    event DuplicateDetected(
        address indexed worker,
        string imageHash,
        address originalWorker
    );

    event StakeForfeited(
        address indexed worker,
        uint256 amount,
        string reason
    );

    event WorkerBlacklisted(
        address indexed worker,
        string reason
    );

    event WorkerUnblacklisted(
        address indexed worker
    );

    // Custom errors
    error RateLimitExceededError(address worker, uint256 currentCount);
    error DuplicateImageDetected(string imageHash, address originalWorker);
    error InvalidMetadata(string reason);
    error WorkerBlacklistedError(address worker);
    error UnauthorizedCaller(address caller);
    error InvalidAddress(address addr);
    error NoStakeToForfeit(address worker);

    // Modifiers
    modifier onlyVerificationContract() {
        if (msg.sender != verificationContractAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier onlyTaskManager() {
        if (msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier onlyAuthorizedContracts() {
        if (msg.sender != verificationContractAddress && msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier notBlacklisted(address worker) {
        if (blacklistedWorkers[worker]) {
            revert WorkerBlacklistedError(worker);
        }
        _;
    }

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set the VerificationContract address
     * @param _verificationContract Address of VerificationContract
     */
    function setVerificationContract(address _verificationContract) external onlyOwner {
        if (_verificationContract == address(0)) {
            revert InvalidAddress(_verificationContract);
        }
        verificationContractAddress = _verificationContract;
    }

    /**
     * @notice Set the TaskManager contract address
     * @param _taskManager Address of TaskManager contract
     */
    function setTaskManager(address _taskManager) external onlyOwner {
        if (_taskManager == address(0)) {
            revert InvalidAddress(_taskManager);
        }
        taskManagerAddress = _taskManager;
    }

    /**
     * @notice Check and record submission for rate limiting
     * @param worker Address of the worker
     * @param taskId ID of the task
     * @param imageHash Hash of the submitted image
     * @param metadataHash Hash of the metadata
     * @return allowed True if submission is allowed
     */
    function checkAndRecordSubmission(
        address worker,
        uint256 taskId,
        string memory imageHash,
        bytes32 metadataHash
    ) external onlyAuthorizedContracts notBlacklisted(worker) returns (bool) {
        // Check rate limit
        _checkRateLimit(worker);

        // Check for duplicate image
        _checkDuplicateImage(worker, imageHash);

        // Validate metadata
        _validateMetadata(metadataHash);

        // Record submission
        _recordSubmission(worker, taskId, imageHash, metadataHash);

        return true;
    }

    /**
     * @notice Check if worker has exceeded daily submission limit
     * @param worker Address of the worker
     */
    function _checkRateLimit(address worker) private {
        uint256 today = _getCurrentDay();
        DailySubmissionCount storage dailyCount = workerDailySubmissions[worker];

        // Reset counter if it's a new day
        if (dailyCount.date != today) {
            dailyCount.date = today;
            dailyCount.count = 0;
        }

        // Check if limit exceeded
        if (dailyCount.count >= MAX_SUBMISSIONS_PER_DAY) {
            emit RateLimitExceeded(worker, dailyCount.count, MAX_SUBMISSIONS_PER_DAY);
            revert RateLimitExceededError(worker, dailyCount.count);
        }

        // Increment counter
        dailyCount.count++;
    }

    /**
     * @notice Check for duplicate image submission
     * @param worker Address of the worker
     * @param imageHash Hash of the image
     */
    function _checkDuplicateImage(address worker, string memory imageHash) private {
        if (usedImageHashes[imageHash]) {
            address originalWorker = imageHashToWorker[imageHash];
            emit DuplicateDetected(worker, imageHash, originalWorker);
            revert DuplicateImageDetected(imageHash, originalWorker);
        }
    }

    /**
     * @notice Validate submission metadata
     * @param metadataHash Hash of the metadata
     */
    function _validateMetadata(bytes32 metadataHash) private pure {
        // Check that metadata hash is not empty
        if (metadataHash == bytes32(0)) {
            revert InvalidMetadata("Metadata hash cannot be empty");
        }

        // Additional validation logic can be added here
        // For example: timestamp validation, location validation, etc.
    }

    /**
     * @notice Record a submission
     * @param worker Address of the worker
     * @param taskId ID of the task
     * @param imageHash Hash of the image
     * @param metadataHash Hash of the metadata
     */
    function _recordSubmission(
        address worker,
        uint256 taskId,
        string memory imageHash,
        bytes32 metadataHash
    ) private {
        // Record submission
        workerSubmissions[worker].push(SubmissionRecord({
            timestamp: block.timestamp,
            imageHash: imageHash,
            metadataHash: metadataHash
        }));

        // Mark image hash as used
        usedImageHashes[imageHash] = true;
        imageHashToWorker[imageHash] = worker;

        emit SubmissionRecorded(worker, taskId, imageHash, block.timestamp);
    }

    /**
     * @notice Forfeit worker's stake on rejected submission
     * @param worker Address of the worker
     * @param reason Reason for forfeiture
     */
    function forfeitStake(
        address worker,
        string memory reason
    ) external onlyVerificationContract {
        uint256 stakeAmount = workerStakes[worker];

        if (stakeAmount == 0) {
            revert NoStakeToForfeit(worker);
        }

        // Reset stake
        workerStakes[worker] = 0;

        emit StakeForfeited(worker, stakeAmount, reason);

        // Consider blacklisting if multiple forfeitures
        // This is a simplified version; production should track forfeiture count
    }

    /**
     * @notice Blacklist a worker
     * @param worker Address of the worker
     * @param reason Reason for blacklisting
     */
    function blacklistWorker(address worker, string memory reason) external onlyOwner {
        blacklistedWorkers[worker] = true;
        emit WorkerBlacklisted(worker, reason);
    }

    /**
     * @notice Remove worker from blacklist
     * @param worker Address of the worker
     */
    function unblacklistWorker(address worker) external onlyOwner {
        blacklistedWorkers[worker] = false;
        emit WorkerUnblacklisted(worker);
    }

    /**
     * @notice Check if image hash is similar to existing submissions
     * @param imageHash Hash of the image to check
     * @param similarityThreshold Similarity threshold percentage
     * @return isSimilar True if similar image found
     * @return originalWorker Address of worker who submitted similar image
     */
    function checkImageSimilarity(
        string memory imageHash,
        uint256 similarityThreshold
    ) external view returns (bool isSimilar, address originalWorker) {
        // This is a simplified version
        // In production, this would use perceptual hashing or other similarity algorithms

        if (usedImageHashes[imageHash]) {
            return (true, imageHashToWorker[imageHash]);
        }

        // For now, we only check exact matches
        // Advanced similarity checking would require off-chain computation
        // and oracle integration

        return (false, address(0));
    }

    /**
     * @notice Get current day timestamp (rounded to start of day)
     * @return dayTimestamp Start of current day
     */
    function _getCurrentDay() private view returns (uint256) {
        return (block.timestamp / ONE_DAY) * ONE_DAY;
    }

    /**
     * @notice Get worker's submission count for today
     * @param worker Address of the worker
     * @return count Number of submissions today
     */
    function getTodaySubmissionCount(address worker) external view returns (uint256) {
        uint256 today = _getCurrentDay();
        DailySubmissionCount storage dailyCount = workerDailySubmissions[worker];

        if (dailyCount.date == today) {
            return dailyCount.count;
        }

        return 0;
    }

    /**
     * @notice Get all submissions for a worker
     * @param worker Address of the worker
     * @return submissions Array of submission records
     */
    function getWorkerSubmissions(address worker) external view returns (SubmissionRecord[] memory) {
        return workerSubmissions[worker];
    }

    /**
     * @notice Check if worker is blacklisted
     * @param worker Address of the worker
     * @return isBlacklisted True if worker is blacklisted
     */
    function isWorkerBlacklisted(address worker) external view returns (bool) {
        return blacklistedWorkers[worker];
    }

    /**
     * @notice Check if image hash has been used
     * @param imageHash Hash of the image
     * @return isUsed True if image hash has been used
     */
    function isImageHashUsed(string memory imageHash) external view returns (bool) {
        return usedImageHashes[imageHash];
    }

    /**
     * @notice Get worker who submitted a specific image hash
     * @param imageHash Hash of the image
     * @return worker Address of the worker
     */
    function getImageHashWorker(string memory imageHash) external view returns (address) {
        return imageHashToWorker[imageHash];
    }

    /**
     * @notice Validate timestamp is recent and not in future
     * @param timestamp Timestamp to validate
     * @param maxAge Maximum age in seconds
     * @return isValid True if timestamp is valid
     */
    function validateTimestamp(uint256 timestamp, uint256 maxAge) external view returns (bool) {
        // Check timestamp is not in the future
        if (timestamp > block.timestamp) {
            return false;
        }

        // Check timestamp is not too old
        if (block.timestamp - timestamp > maxAge) {
            return false;
        }

        return true;
    }
}

contracts/BountyPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BountyPool
 * @notice Manages escrow of funds, reward distribution, and platform fees
 */
contract BountyPool is Ownable, ReentrancyGuard {
    // Constants
    uint256 public constant PLATFORM_FEE_PERCENTAGE = 250; // 2.5% (basis points)
    uint256 public constant EXPIRED_TASK_FEE_PERCENTAGE = 500; // 5% (basis points)
    uint256 public constant BASIS_POINTS = 10000; // 100%

    // State variables
    mapping(uint256 => uint256) public taskBounties;
    uint256 public accumulatedFees;
    address public taskManagerAddress;
    address public verificationContractAddress;

    // Events
    event BountyDeposited(uint256 indexed taskId, address indexed creator, uint256 amount);
    event RewardDistributed(uint256 indexed taskId, address indexed worker, uint256 amount, uint256 fee);
    event BountyRefunded(uint256 indexed taskId, address indexed creator, uint256 amount, uint256 fee);
    event PlatformFeesWithdrawn(address indexed owner, uint256 amount);
    event TaskManagerUpdated(address indexed newTaskManager);
    event VerificationContractUpdated(address indexed newVerificationContract);

    // Custom errors
    error InsufficientBounty(uint256 taskId, uint256 required, uint256 available);
    error UnauthorizedCaller(address caller);
    error TransferFailed(address recipient, uint256 amount);
    error InvalidAddress(address addr);
    error NoFeesToWithdraw();

    // Modifiers
    modifier onlyTaskManager() {
        if (msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier onlyVerificationContract() {
        if (msg.sender != verificationContractAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set the TaskManager contract address
     * @param _taskManager Address of TaskManager contract
     */
    function setTaskManager(address _taskManager) external onlyOwner {
        if (_taskManager == address(0)) {
            revert InvalidAddress(_taskManager);
        }
        taskManagerAddress = _taskManager;
        emit TaskManagerUpdated(_taskManager);
    }

    /**
     * @notice Set the VerificationContract address
     * @param _verificationContract Address of VerificationContract
     */
    function setVerificationContract(address _verificationContract) external onlyOwner {
        if (_verificationContract == address(0)) {
            revert InvalidAddress(_verificationContract);
        }
        verificationContractAddress = _verificationContract;
        emit VerificationContractUpdated(_verificationContract);
    }

    /**
     * @notice Deposit bounty for a task
     * @param taskId The ID of the task
     */
    function depositBounty(uint256 taskId) external payable onlyTaskManager {
        taskBounties[taskId] += msg.value;
        emit BountyDeposited(taskId, tx.origin, msg.value);
    }

    /**
     * @notice Distribute reward to a worker
     * @param taskId The ID of the task
     * @param worker Address of the worker
     * @param amount Amount to distribute
     */
    function distributeReward(
        uint256 taskId,
        address worker,
        uint256 amount
    ) external nonReentrant onlyVerificationContract {
        if (taskBounties[taskId] < amount) {
            revert InsufficientBounty(taskId, amount, taskBounties[taskId]);
        }

        // Calculate platform fee (2.5%)
        uint256 platformFee = (amount * PLATFORM_FEE_PERCENTAGE) / BASIS_POINTS;
        uint256 workerReward = amount - platformFee;

        // Update bounty balance
        taskBounties[taskId] -= amount;

        // Accumulate platform fee
        accumulatedFees += platformFee;

        // Transfer reward to worker
        (bool success, ) = payable(worker).call{value: workerReward}("");
        if (!success) {
            revert TransferFailed(worker, workerReward);
        }

        emit RewardDistributed(taskId, worker, workerReward, platformFee);
    }

    /**
     * @notice Refund bounty for an expired task
     * @param taskId The ID of the task
     * @param creator Address of the task creator
     * @param amount Amount to refund
     */
    function refundBounty(
        uint256 taskId,
        address creator,
        uint256 amount
    ) external nonReentrant onlyTaskManager {
        if (taskBounties[taskId] < amount) {
            revert InsufficientBounty(taskId, amount, taskBounties[taskId]);
        }

        // Calculate platform fee for expired task (5%)
        uint256 platformFee = (amount * EXPIRED_TASK_FEE_PERCENTAGE) / BASIS_POINTS;
        uint256 refundAmount = amount - platformFee;

        // Update bounty balance
        taskBounties[taskId] -= amount;

        // Accumulate platform fee
        accumulatedFees += platformFee;

        // Transfer refund to creator
        (bool success, ) = payable(creator).call{value: refundAmount}("");
        if (!success) {
            revert TransferFailed(creator, refundAmount);
        }

        emit BountyRefunded(taskId, creator, refundAmount, platformFee);
    }

    /**
     * @notice Withdraw accumulated platform fees
     */
    function withdrawPlatformFees() external nonReentrant onlyOwner {
        uint256 amount = accumulatedFees;

        if (amount == 0) {
            revert NoFeesToWithdraw();
        }

        accumulatedFees = 0;

        (bool success, ) = payable(owner()).call{value: amount}("");
        if (!success) {
            revert TransferFailed(owner(), amount);
        }

        emit PlatformFeesWithdrawn(owner(), amount);
    }

    /**
     * @notice Get bounty amount for a task
     * @param taskId The ID of the task
     * @return amount The bounty amount
     */
    function getTaskBounty(uint256 taskId) external view returns (uint256) {
        return taskBounties[taskId];
    }

    /**
     * @notice Get accumulated platform fees
     * @return amount The accumulated fees
     */
    function getAccumulatedFees() external view returns (uint256) {
        return accumulatedFees;
    }

    /**
     * @notice Calculate platform fee for a given amount
     * @param amount The amount to calculate fee for
     * @return fee The platform fee
     */
    function calculatePlatformFee(uint256 amount) external pure returns (uint256) {
        return (amount * PLATFORM_FEE_PERCENTAGE) / BASIS_POINTS;
    }

    /**
     * @notice Calculate expired task fee for a given amount
     * @param amount The amount to calculate fee for
     * @return fee The expired task fee
     */
    function calculateExpiredTaskFee(uint256 amount) external pure returns (uint256) {
        return (amount * EXPIRED_TASK_FEE_PERCENTAGE) / BASIS_POINTS;
    }

    /**
     * @notice Receive function to accept ETH
     */
    receive() external payable {}
}

contracts/ReputationContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ReputationContract
 * @notice Tracks worker and verifier reputation scores
 */
contract ReputationContract is Ownable {
    // Enums
    enum TaskCategory {
        PHOTO_VERIFICATION,
        LOCATION_CHECK,
        SURVEY,
        PRICE_MONITORING,
        BUSINESS_HOURS
    }

    // Structs
    struct CategoryStats {
        uint256 successCount;
        uint256 totalCount;
        bool hasBadge;
    }

    struct ReputationData {
        uint256 score;
        uint256 tasksCompleted;
        uint256 tasksRejected;
        uint256 verificationsPerformed;
        uint256 accurateVerifications;
        mapping(TaskCategory => CategoryStats) categoryStats;
    }

    // Constants
    uint256 public constant INITIAL_REPUTATION = 50;
    uint256 public constant REPUTATION_INCREASE = 5;
    uint256 public constant REPUTATION_DECREASE = 5;
    uint256 public constant BADGE_TASK_THRESHOLD = 10;
    uint256 public constant BADGE_SUCCESS_RATE_THRESHOLD = 90; // 90%
    uint256 public constant REPUTATION_MULTIPLIER_PERCENTAGE = 1000; // 10% (basis points)
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant HIGH_REPUTATION_THRESHOLD = 80;
    uint256 public constant MAX_REPUTATION = 100;
    uint256 public constant MIN_REPUTATION = 0;

    // State variables
    mapping(address => ReputationData) private reputations;
    address public verificationContractAddress;
    address public taskManagerAddress;

    // Events
    event ReputationInitialized(address indexed user, uint256 initialScore);
    event WorkerReputationUpdated(address indexed worker, bool successful, uint256 newScore);
    event VerifierReputationUpdated(address indexed verifier, bool accurate, uint256 newScore);
    event CategoryBadgeAwarded(address indexed worker, TaskCategory category);
    event VerificationContractUpdated(address indexed newContract);
    event TaskManagerUpdated(address indexed newContract);

    // Custom errors
    error UnauthorizedCaller(address caller);
    error InvalidAddress(address addr);
    error ReputationAlreadyInitialized(address user);

    // Modifiers
    modifier onlyVerificationContract() {
        if (msg.sender != verificationContractAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier onlyTaskManager() {
        if (msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    modifier onlyAuthorizedContracts() {
        if (msg.sender != verificationContractAddress && msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set the VerificationContract address
     * @param _verificationContract Address of VerificationContract
     */
    function setVerificationContract(address _verificationContract) external onlyOwner {
        if (_verificationContract == address(0)) {
            revert InvalidAddress(_verificationContract);
        }
        verificationContractAddress = _verificationContract;
        emit VerificationContractUpdated(_verificationContract);
    }

    /**
     * @notice Set the TaskManager contract address
     * @param _taskManager Address of TaskManager contract
     */
    function setTaskManager(address _taskManager) external onlyOwner {
        if (_taskManager == address(0)) {
            revert InvalidAddress(_taskManager);
        }
        taskManagerAddress = _taskManager;
        emit TaskManagerUpdated(_taskManager);
    }

    /**
     * @notice Initialize reputation for a new user
     * @param user Address of the user
     */
    function initializeReputation(address user) external {
        ReputationData storage rep = reputations[user];

        if (rep.score != 0) {
            revert ReputationAlreadyInitialized(user);
        }

        rep.score = INITIAL_REPUTATION;

        emit ReputationInitialized(user, INITIAL_REPUTATION);
    }

    /**
     * @notice Update worker reputation based on task outcome
     * @param worker Address of the worker
     * @param successful Whether the task was successful
     */
    function updateWorkerReputation(
        address worker,
        bool successful
    ) external onlyVerificationContract {
        ReputationData storage rep = reputations[worker];

        // Initialize if first time
        if (rep.score == 0) {
            rep.score = INITIAL_REPUTATION;
        }

        if (successful) {
            // Increase reputation (capped at MAX_REPUTATION)
            if (rep.score + REPUTATION_INCREASE <= MAX_REPUTATION) {
                rep.score += REPUTATION_INCREASE;
            } else {
                rep.score = MAX_REPUTATION;
            }
            rep.tasksCompleted++;
        } else {
            // Decrease reputation (floored at MIN_REPUTATION)
            if (rep.score >= REPUTATION_DECREASE) {
                rep.score -= REPUTATION_DECREASE;
            } else {
                rep.score = MIN_REPUTATION;
            }
            rep.tasksRejected++;
        }

        emit WorkerReputationUpdated(worker, successful, rep.score);
    }

    /**
     * @notice Update worker reputation with category tracking
     * @param worker Address of the worker
     * @param category Task category
     * @param successful Whether the task was successful
     */
    function updateWorkerReputationWithCategory(
        address worker,
        TaskCategory category,
        bool successful
    ) external onlyAuthorizedContracts {
        ReputationData storage rep = reputations[worker];

        // Initialize if first time
        if (rep.score == 0) {
            rep.score = INITIAL_REPUTATION;
        }

        // Update overall reputation
        if (successful) {
            if (rep.score + REPUTATION_INCREASE <= MAX_REPUTATION) {
                rep.score += REPUTATION_INCREASE;
            } else {
                rep.score = MAX_REPUTATION;
            }
            rep.tasksCompleted++;
        } else {
            if (rep.score >= REPUTATION_DECREASE) {
                rep.score -= REPUTATION_DECREASE;
            } else {
                rep.score = MIN_REPUTATION;
            }
            rep.tasksRejected++;
        }

        // Update category stats
        CategoryStats storage catStats = rep.categoryStats[category];
        catStats.totalCount++;
        if (successful) {
            catStats.successCount++;
        }

        // Check for badge award
        if (!catStats.hasBadge && _shouldAwardBadge(catStats)) {
            catStats.hasBadge = true;
            emit CategoryBadgeAwarded(worker, category);
        }

        emit WorkerReputationUpdated(worker, successful, rep.score);
    }

    /**
     * @notice Update verifier reputation based on vote accuracy
     * @param verifier Address of the verifier
     * @param accurateVote Whether the vote was accurate
     */
    function updateVerifierReputation(
        address verifier,
        bool accurateVote
    ) external onlyVerificationContract {
        ReputationData storage rep = reputations[verifier];

        // Initialize if first time
        if (rep.score == 0) {
            rep.score = INITIAL_REPUTATION;
        }

        rep.verificationsPerformed++;

        if (accurateVote) {
            // Increase reputation for accurate verification
            if (rep.score + REPUTATION_INCREASE <= MAX_REPUTATION) {
                rep.score += REPUTATION_INCREASE;
            } else {
                rep.score = MAX_REPUTATION;
            }
            rep.accurateVerifications++;
        } else {
            // Decrease reputation for inaccurate verification
            if (rep.score >= REPUTATION_DECREASE) {
                rep.score -= REPUTATION_DECREASE;
            } else {
                rep.score = MIN_REPUTATION;
            }
        }

        emit VerifierReputationUpdated(verifier, accurateVote, rep.score);
    }

    /**
     * @notice Check if a badge should be awarded
     * @param stats Category statistics
     * @return shouldAward True if badge should be awarded
     */
    function _shouldAwardBadge(CategoryStats storage stats) private view returns (bool) {
        if (stats.totalCount < BADGE_TASK_THRESHOLD) {
            return false;
        }

        uint256 successRate = (stats.successCount * 100) / stats.totalCount;
        return successRate >= BADGE_SUCCESS_RATE_THRESHOLD;
    }

    /**
     * @notice Get reputation score for a user
     * @param user Address of the user
     * @return score The reputation score
     */
    function getReputationScore(address user) external view returns (uint256) {
        uint256 score = reputations[user].score;
        return score == 0 ? INITIAL_REPUTATION : score;
    }

    /**
     * @notice Get full reputation data for a user
     * @param user Address of the user
     * @return score Reputation score
     * @return tasksCompleted Number of completed tasks
     * @return tasksRejected Number of rejected tasks
     * @return verificationsPerformed Number of verifications performed
     * @return accurateVerifications Number of accurate verifications
     */
    function getReputationData(address user) external view returns (
        uint256 score,
        uint256 tasksCompleted,
        uint256 tasksRejected,
        uint256 verificationsPerformed,
        uint256 accurateVerifications
    ) {
        ReputationData storage rep = reputations[user];
        score = rep.score == 0 ? INITIAL_REPUTATION : rep.score;
        tasksCompleted = rep.tasksCompleted;
        tasksRejected = rep.tasksRejected;
        verificationsPerformed = rep.verificationsPerformed;
        accurateVerifications = rep.accurateVerifications;
    }

    /**
     * @notice Check if user has a category badge
     * @param user Address of the user
     * @param category Task category
     * @return hasBadge True if user has the badge
     */
    function hasCategoryBadge(address user, TaskCategory category) external view returns (bool) {
        return reputations[user].categoryStats[category].hasBadge;
    }

    /**
     * @notice Get category statistics for a user
     * @param user Address of the user
     * @param category Task category
     * @return successCount Number of successful tasks
     * @return totalCount Total number of tasks
     * @return hasBadge Whether user has badge
     * @return successRate Success rate percentage
     */
    function getCategoryStats(address user, TaskCategory category) external view returns (
        uint256 successCount,
        uint256 totalCount,
        bool hasBadge,
        uint256 successRate
    ) {
        CategoryStats storage stats = reputations[user].categoryStats[category];
        successCount = stats.successCount;
        totalCount = stats.totalCount;
        hasBadge = stats.hasBadge;
        successRate = totalCount > 0 ? (successCount * 100) / totalCount : 0;
    }

    /**
     * @notice Calculate reputation multiplier for a user
     * @param user Address of the user
     * @param category Task category
     * @return multiplier The multiplier in basis points (10000 = 100%)
     */
    function getReputationMultiplier(address user, TaskCategory category) external view returns (uint256) {
        if (reputations[user].categoryStats[category].hasBadge) {
            return BASIS_POINTS + REPUTATION_MULTIPLIER_PERCENTAGE; // 110%
        }
        return BASIS_POINTS; // 100%
    }

    /**
     * @notice Check if user has priority access (reputation >= 80)
     * @param user Address of the user
     * @return hasPriority True if user has priority access
     */
    function hasPriorityAccess(address user) external view returns (bool) {
        uint256 score = reputations[user].score;
        if (score == 0) {
            score = INITIAL_REPUTATION;
        }
        return score >= HIGH_REPUTATION_THRESHOLD;
    }

    /**
     * @notice Get success rate for a user
     * @param user Address of the user
     * @return successRate Success rate percentage
     */
    function getSuccessRate(address user) external view returns (uint256) {
        ReputationData storage rep = reputations[user];
        uint256 totalTasks = rep.tasksCompleted + rep.tasksRejected;

        if (totalTasks == 0) {
            return 0;
        }

        return (rep.tasksCompleted * 100) / totalTasks;
    }

    /**
     * @notice Get verification accuracy rate for a user
     * @param user Address of the user
     * @return accuracyRate Accuracy rate percentage
     */
    function getVerificationAccuracy(address user) external view returns (uint256) {
        ReputationData storage rep = reputations[user];

        if (rep.verificationsPerformed == 0) {
            return 0;
        }

        return (rep.accurateVerifications * 100) / rep.verificationsPerformed;
    }
}

contracts/TaskManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title TaskManager
 * @notice Manages task lifecycle, creation, assignment, and completion
 */
contract TaskManager is Ownable, ReentrancyGuard {
    // Enums
    enum TaskCategory {
        PHOTO_VERIFICATION,
        LOCATION_CHECK,
        SURVEY,
        PRICE_MONITORING,
        BUSINESS_HOURS
    }

    enum TaskStatus {
        ACTIVE,
        IN_PROGRESS,
        COMPLETED,
        EXPIRED,
        CANCELLED
    }

    // Structs
    struct Location {
        int256 latitude;  // Scaled by 1e6
        int256 longitude; // Scaled by 1e6
        uint256 radius;   // In meters
    }

    struct TaskRequirements {
        uint8 photoCount;
        bool requiresLocation;
        uint256 minReputation;
        TaskCategory requiredBadge;
    }

    struct Task {
        uint256 id;
        address creator;
        string description;
        TaskCategory category;
        uint256 bountyAmount;
        uint256 maxWorkers;
        Location location;
        uint256 deadline;
        TaskStatus status;
        TaskRequirements requirements;
        uint256 submissionCount;
        uint256 verifiedCount;
        uint256 createdAt;
    }

    struct TaskClaim {
        address worker;
        uint256 claimedAt;
        bool completed;
    }

    // Constants
    uint256 public constant MINIMUM_BOUNTY = 0.5 ether; // 0.5 cUSD
    uint256 public constant MAX_ACTIVE_TASKS_PER_WORKER = 3;
    uint256 public constant TASK_COMPLETION_TIMEOUT = 24 hours;

    // State variables
    uint256 private taskIdCounter;
    mapping(uint256 => Task) public tasks;
    mapping(address => uint256[]) public workerActiveTasks;
    mapping(uint256 => mapping(address => TaskClaim)) public taskClaims;
    mapping(uint256 => address[]) public taskWorkers;

    // Events
    event TaskCreated(
        uint256 indexed taskId,
        address indexed creator,
        TaskCategory category,
        uint256 bountyAmount,
        uint256 deadline
    );

    event TaskClaimed(
        uint256 indexed taskId,
        address indexed worker,
        uint256 claimedAt
    );

    event TaskSubmitted(
        uint256 indexed taskId,
        address indexed worker,
        string ipfsHash,
        uint256 submittedAt
    );

    event TaskExpired(
        uint256 indexed taskId,
        uint256 expiredAt
    );

    // Custom errors
    error InsufficientBounty(uint256 provided, uint256 required);
    error TaskNotActive(uint256 taskId);
    error WorkerAlreadyClaimed(address worker, uint256 taskId);
    error MaxActiveTasksReached(address worker);
    error OutsideLocationRadius(int256 requiredLat, int256 requiredLon, int256 providedLat, int256 providedLon);
    error DeadlineExpired(uint256 taskId, uint256 deadline);
    error UnauthorizedAccess(address caller);
    error InvalidTaskId(uint256 taskId);
    error TaskNotClaimed(address worker, uint256 taskId);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Create a new task
     * @param description Task description
     * @param category Task category
     * @param bountyAmount Reward amount in wei
     * @param maxWorkers Maximum number of workers
     * @param location Task location with radius
     * @param deadline Task deadline timestamp
     * @param requirements Task requirements
     * @return taskId The ID of the created task
     */
    function createTask(
        string memory description,
        TaskCategory category,
        uint256 bountyAmount,
        uint256 maxWorkers,
        Location memory location,
        uint256 deadline,
        TaskRequirements memory requirements
    ) external payable returns (uint256) {
        if (bountyAmount < MINIMUM_BOUNTY) {
            revert InsufficientBounty(bountyAmount, MINIMUM_BOUNTY);
        }

        if (deadline <= block.timestamp) {
            revert DeadlineExpired(0, deadline);
        }

        uint256 taskId = taskIdCounter++;

        tasks[taskId] = Task({
            id: taskId,
            creator: msg.sender,
            description: description,
            category: category,
            bountyAmount: bountyAmount,
            maxWorkers: maxWorkers,
            location: location,
            deadline: deadline,
            status: TaskStatus.ACTIVE,
            requirements: requirements,
            submissionCount: 0,
            verifiedCount: 0,
            createdAt: block.timestamp
        });

        emit TaskCreated(taskId, msg.sender, category, bountyAmount, deadline);

        return taskId;
    }

    /**
     * @notice Claim a task
     * @param taskId The ID of the task to claim
     * @return success True if claim was successful
     */
    function claimTask(uint256 taskId) external nonReentrant returns (bool) {
        Task storage task = tasks[taskId];

        if (task.id != taskId) {
            revert InvalidTaskId(taskId);
        }

        if (task.status != TaskStatus.ACTIVE) {
            revert TaskNotActive(taskId);
        }

        if (block.timestamp > task.deadline) {
            revert DeadlineExpired(taskId, task.deadline);
        }

        if (taskClaims[taskId][msg.sender].worker != address(0)) {
            revert WorkerAlreadyClaimed(msg.sender, taskId);
        }

        if (workerActiveTasks[msg.sender].length >= MAX_ACTIVE_TASKS_PER_WORKER) {
            revert MaxActiveTasksReached(msg.sender);
        }

        // Check if max workers reached
        if (taskWorkers[taskId].length >= task.maxWorkers) {
            revert TaskNotActive(taskId);
        }

        // Create claim
        taskClaims[taskId][msg.sender] = TaskClaim({
            worker: msg.sender,
            claimedAt: block.timestamp,
            completed: false
        });

        // Add to worker's active tasks
        workerActiveTasks[msg.sender].push(taskId);

        // Add to task workers
        taskWorkers[taskId].push(msg.sender);

        // Update task status if first claim
        if (task.status == TaskStatus.ACTIVE && taskWorkers[taskId].length > 0) {
            task.status = TaskStatus.IN_PROGRESS;
        }

        emit TaskClaimed(taskId, msg.sender, block.timestamp);

        return true;
    }

    /**
     * @notice Submit task completion
     * @param taskId The ID of the task
     * @param ipfsHash IPFS hash of submission data
     * @param submissionLocation Location where task was completed
     * @return submissionId The ID of the submission
     */
    function submitTaskCompletion(
        uint256 taskId,
        string memory ipfsHash,
        Location memory submissionLocation
    ) external nonReentrant returns (uint256) {
        Task storage task = tasks[taskId];

        if (task.id != taskId) {
            revert InvalidTaskId(taskId);
        }

        if (taskClaims[taskId][msg.sender].worker == address(0)) {
            revert TaskNotClaimed(msg.sender, taskId);
        }

        if (block.timestamp > task.deadline) {
            revert DeadlineExpired(taskId, task.deadline);
        }

        // Check if claim has expired (24 hours)
        TaskClaim storage claim = taskClaims[taskId][msg.sender];
        if (block.timestamp > claim.claimedAt + TASK_COMPLETION_TIMEOUT) {
            revert DeadlineExpired(taskId, claim.claimedAt + TASK_COMPLETION_TIMEOUT);
        }

        // Validate location if required
        if (task.requirements.requiresLocation) {
            if (!_isWithinRadius(task.location, submissionLocation)) {
                revert OutsideLocationRadius(
                    task.location.latitude,
                    task.location.longitude,
                    submissionLocation.latitude,
                    submissionLocation.longitude
                );
            }
        }

        // Mark claim as completed
        claim.completed = true;

        // Remove from worker's active tasks
        _removeActiveTask(msg.sender, taskId);

        // Increment submission count
        task.submissionCount++;

        uint256 submissionId = task.submissionCount;

        emit TaskSubmitted(taskId, msg.sender, ipfsHash, block.timestamp);

        return submissionId;
    }

    /**
     * @notice Expire a task that has passed its deadline
     * @param taskId The ID of the task to expire
     * @return success True if expiration was successful
     */
    function expireTask(uint256 taskId) external returns (bool) {
        Task storage task = tasks[taskId];

        if (task.id != taskId) {
            revert InvalidTaskId(taskId);
        }

        if (block.timestamp <= task.deadline) {
            revert DeadlineExpired(taskId, task.deadline);
        }

        if (task.status == TaskStatus.EXPIRED || task.status == TaskStatus.COMPLETED) {
            return false;
        }

        task.status = TaskStatus.EXPIRED;

        emit TaskExpired(taskId, block.timestamp);

        return true;
    }

    /**
     * @notice Check if a location is within the required radius
     * @param required Required location
     * @param provided Provided location
     * @return isWithin True if within radius
     */
    function _isWithinRadius(
        Location memory required,
        Location memory provided
    ) private pure returns (bool) {
        // Simple distance calculation (Haversine would be more accurate but more expensive)
        int256 latDiff = required.latitude - provided.latitude;
        int256 lonDiff = required.longitude - provided.longitude;

        // Calculate squared distance (avoiding sqrt for gas efficiency)
        uint256 distanceSquared = uint256(latDiff * latDiff + lonDiff * lonDiff);
        uint256 radiusSquared = required.radius * required.radius;

        // Scale factor for lat/lon (1e6)
        uint256 scaleFactor = 1e12; // 1e6 * 1e6

        // Approximate: 1 degree  111km, so we scale accordingly
        // This is a simplified check; production should use proper geospatial calculation
        return distanceSquared <= (radiusSquared * scaleFactor) / (111000 * 111000);
    }

    /**
     * @notice Remove a task from worker's active tasks
     * @param worker Worker address
     * @param taskId Task ID to remove
     */
    function _removeActiveTask(address worker, uint256 taskId) private {
        uint256[] storage activeTasks = workerActiveTasks[worker];
        for (uint256 i = 0; i < activeTasks.length; i++) {
            if (activeTasks[i] == taskId) {
                activeTasks[i] = activeTasks[activeTasks.length - 1];
                activeTasks.pop();
                break;
            }
        }
    }

    /**
     * @notice Get task details
     * @param taskId The ID of the task
     * @return task The task details
     */
    function getTask(uint256 taskId) external view returns (Task memory) {
        return tasks[taskId];
    }

    /**
     * @notice Get worker's active tasks
     * @param worker Worker address
     * @return taskIds Array of active task IDs
     */
    function getWorkerActiveTasks(address worker) external view returns (uint256[] memory) {
        return workerActiveTasks[worker];
    }

    /**
     * @notice Get workers for a task
     * @param taskId Task ID
     * @return workers Array of worker addresses
     */
    function getTaskWorkers(uint256 taskId) external view returns (address[] memory) {
        return taskWorkers[taskId];
    }

    /**
     * @notice Check if worker has claimed a task
     * @param taskId Task ID
     * @param worker Worker address
     * @return claimed True if worker has claimed the task
     */
    function hasWorkerClaimed(uint256 taskId, address worker) external view returns (bool) {
        return taskClaims[taskId][worker].worker != address(0);
    }
}

contracts/VerificationContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface IBountyPool {
    function distributeReward(uint256 taskId, address worker, uint256 amount) external;
}

interface IReputationContract {
    function updateWorkerReputation(address worker, bool successful) external;
    function updateVerifierReputation(address verifier, bool accurateVote) external;
}

/**
 * @title VerificationContract
 * @notice Implements peer verification consensus mechanism
 */
contract VerificationContract is Ownable, ReentrancyGuard {
    // Enums
    enum VerificationStatus {
        PENDING,
        VERIFIED,
        REJECTED,
        DISPUTED
    }

    // Structs
    struct Location {
        int256 latitude;
        int256 longitude;
        uint256 radius;
    }

    struct Submission {
        uint256 id;
        uint256 taskId;
        address worker;
        string ipfsHash;
        uint256 timestamp;
        Location location;
        VerificationStatus status;
        uint256 approvalCount;
        uint256 rejectionCount;
        uint256 totalVotes;
        uint256 bountyAmount;
        bool rewardDistributed;
    }

    struct Vote {
        bool hasVoted;
        bool approved;
        uint256 timestamp;
        uint256 stake;
    }

    // Constants
    uint256 public constant VERIFICATION_STAKE = 0.1 ether; // 0.1 cUSD
    uint256 public constant CONSENSUS_THRESHOLD = 3;
    uint256 public constant MAX_VERIFIERS = 7;
    uint256 public constant VERIFICATION_REWARD_PERCENTAGE = 1000; // 10% (basis points)
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant REWARD_DISTRIBUTION_DELAY = 5 minutes;

    // State variables
    uint256 private submissionIdCounter;
    mapping(uint256 => Submission) public submissions;
    mapping(uint256 => mapping(address => Vote)) public verificationVotes;
    mapping(uint256 => address[]) public submissionVerifiers;
    mapping(uint256 => uint256) public consensusReachedAt;

    address public bountyPoolAddress;
    address public reputationContractAddress;
    address public taskManagerAddress;

    // Events
    event SubmissionCreated(
        uint256 indexed submissionId,
        uint256 indexed taskId,
        address indexed worker,
        string ipfsHash
    );

    event VerificationStaked(
        uint256 indexed submissionId,
        address indexed verifier,
        uint256 amount
    );

    event VoteSubmitted(
        uint256 indexed submissionId,
        address indexed verifier,
        bool approved
    );

    event ConsensusReached(
        uint256 indexed submissionId,
        VerificationStatus status,
        uint256 timestamp
    );

    event RewardDistributed(
        uint256 indexed submissionId,
        address indexed worker,
        uint256 amount
    );

    event VerificationRewardsDistributed(
        uint256 indexed submissionId,
        uint256 totalReward
    );

    event DisputeEscalated(
        uint256 indexed submissionId,
        uint256 currentVotes
    );

    // Custom errors
    error InsufficientStake(uint256 provided, uint256 required);
    error AlreadyVoted(address verifier, uint256 submissionId);
    error SubmissionNotFound(uint256 submissionId);
    error AlreadyVerified(uint256 submissionId);
    error ConsensusNotReached(uint256 submissionId);
    error MaxVerifiersReached(uint256 submissionId);
    error RewardAlreadyDistributed(uint256 submissionId);
    error RewardDistributionTooEarly(uint256 submissionId, uint256 timeRemaining);
    error UnauthorizedCaller(address caller);
    error InvalidAddress(address addr);
    error WorkerCannotVerifyOwnSubmission(address worker);

    // Modifiers
    modifier onlyTaskManager() {
        if (msg.sender != taskManagerAddress) {
            revert UnauthorizedCaller(msg.sender);
        }
        _;
    }

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set the BountyPool contract address
     * @param _bountyPool Address of BountyPool contract
     */
    function setBountyPool(address _bountyPool) external onlyOwner {
        if (_bountyPool == address(0)) {
            revert InvalidAddress(_bountyPool);
        }
        bountyPoolAddress = _bountyPool;
    }

    /**
     * @notice Set the ReputationContract address
     * @param _reputationContract Address of ReputationContract
     */
    function setReputationContract(address _reputationContract) external onlyOwner {
        if (_reputationContract == address(0)) {
            revert InvalidAddress(_reputationContract);
        }
        reputationContractAddress = _reputationContract;
    }

    /**
     * @notice Set the TaskManager contract address
     * @param _taskManager Address of TaskManager contract
     */
    function setTaskManager(address _taskManager) external onlyOwner {
        if (_taskManager == address(0)) {
            revert InvalidAddress(_taskManager);
        }
        taskManagerAddress = _taskManager;
    }

    /**
     * @notice Create a new submission
     * @param taskId The ID of the task
     * @param worker Address of the worker
     * @param ipfsHash IPFS hash of submission data
     * @param location Location of submission
     * @param bountyAmount Bounty amount for the task
     * @return submissionId The ID of the created submission
     */
    function createSubmission(
        uint256 taskId,
        address worker,
        string memory ipfsHash,
        Location memory location,
        uint256 bountyAmount
    ) external onlyTaskManager returns (uint256) {
        uint256 submissionId = submissionIdCounter++;

        submissions[submissionId] = Submission({
            id: submissionId,
            taskId: taskId,
            worker: worker,
            ipfsHash: ipfsHash,
            timestamp: block.timestamp,
            location: location,
            status: VerificationStatus.PENDING,
            approvalCount: 0,
            rejectionCount: 0,
            totalVotes: 0,
            bountyAmount: bountyAmount,
            rewardDistributed: false
        });

        emit SubmissionCreated(submissionId, taskId, worker, ipfsHash);

        return submissionId;
    }

    /**
     * @notice Stake for verification
     * @param submissionId The ID of the submission to verify
     */
    function stakeForVerification(uint256 submissionId) external payable nonReentrant {
        Submission storage submission = submissions[submissionId];

        if (submission.id != submissionId) {
            revert SubmissionNotFound(submissionId);
        }

        if (submission.status != VerificationStatus.PENDING && submission.status != VerificationStatus.DISPUTED) {
            revert AlreadyVerified(submissionId);
        }

        if (verificationVotes[submissionId][msg.sender].hasVoted) {
            revert AlreadyVoted(msg.sender, submissionId);
        }

        if (submissionVerifiers[submissionId].length >= MAX_VERIFIERS) {
            revert MaxVerifiersReached(submissionId);
        }

        if (msg.sender == submission.worker) {
            revert WorkerCannotVerifyOwnSubmission(msg.sender);
        }

        if (msg.value < VERIFICATION_STAKE) {
            revert InsufficientStake(msg.value, VERIFICATION_STAKE);
        }

        // Record stake
        verificationVotes[submissionId][msg.sender].stake = msg.value;
        submissionVerifiers[submissionId].push(msg.sender);

        emit VerificationStaked(submissionId, msg.sender, msg.value);
    }

    /**
     * @notice Submit verification vote
     * @param submissionId The ID of the submission
     * @param approved Whether the submission is approved
     * @param feedback Optional feedback string
     */
    function submitVerification(
        uint256 submissionId,
        bool approved,
        string memory feedback
    ) external nonReentrant {
        Submission storage submission = submissions[submissionId];
        Vote storage vote = verificationVotes[submissionId][msg.sender];

        if (submission.id != submissionId) {
            revert SubmissionNotFound(submissionId);
        }

        if (vote.stake == 0) {
            revert InsufficientStake(0, VERIFICATION_STAKE);
        }

        if (vote.hasVoted) {
            revert AlreadyVoted(msg.sender, submissionId);
        }

        // Record vote
        vote.hasVoted = true;
        vote.approved = approved;
        vote.timestamp = block.timestamp;

        // Update counts
        submission.totalVotes++;
        if (approved) {
            submission.approvalCount++;
        } else {
            submission.rejectionCount++;
        }

        emit VoteSubmitted(submissionId, msg.sender, approved);

        // Check for consensus
        _checkConsensus(submissionId);
    }

    /**
     * @notice Check if consensus has been reached
     * @param submissionId The ID of the submission
     */
    function _checkConsensus(uint256 submissionId) private {
        Submission storage submission = submissions[submissionId];

        // Check if consensus threshold reached
        if (submission.approvalCount >= CONSENSUS_THRESHOLD) {
            submission.status = VerificationStatus.VERIFIED;
            consensusReachedAt[submissionId] = block.timestamp;
            emit ConsensusReached(submissionId, VerificationStatus.VERIFIED, block.timestamp);

            // Update worker reputation
            if (reputationContractAddress != address(0)) {
                IReputationContract(reputationContractAddress).updateWorkerReputation(
                    submission.worker,
                    true
                );
            }
        } else if (submission.rejectionCount >= CONSENSUS_THRESHOLD) {
            submission.status = VerificationStatus.REJECTED;
            consensusReachedAt[submissionId] = block.timestamp;
            emit ConsensusReached(submissionId, VerificationStatus.REJECTED, block.timestamp);

            // Update worker reputation
            if (reputationContractAddress != address(0)) {
                IReputationContract(reputationContractAddress).updateWorkerReputation(
                    submission.worker,
                    false
                );
            }

            // Forfeit worker stake if they had one
            // (Implementation would depend on staking mechanism)
        } else if (submission.totalVotes >= MAX_VERIFIERS) {
            // Escalation: max verifiers reached without consensus
            submission.status = VerificationStatus.DISPUTED;
            emit DisputeEscalated(submissionId, submission.totalVotes);
        }
    }

    /**
     * @notice Distribute rewards after consensus
     * @param submissionId The ID of the submission
     */
    function distributeVerificationRewards(uint256 submissionId) external nonReentrant {
        Submission storage submission = submissions[submissionId];

        if (submission.id != submissionId) {
            revert SubmissionNotFound(submissionId);
        }

        if (submission.status != VerificationStatus.VERIFIED && submission.status != VerificationStatus.REJECTED) {
            revert ConsensusNotReached(submissionId);
        }

        if (submission.rewardDistributed) {
            revert RewardAlreadyDistributed(submissionId);
        }

        uint256 timeSinceConsensus = block.timestamp - consensusReachedAt[submissionId];
        if (timeSinceConsensus < REWARD_DISTRIBUTION_DELAY) {
            revert RewardDistributionTooEarly(submissionId, REWARD_DISTRIBUTION_DELAY - timeSinceConsensus);
        }

        submission.rewardDistributed = true;

        // Distribute worker reward if verified
        if (submission.status == VerificationStatus.VERIFIED && bountyPoolAddress != address(0)) {
            IBountyPool(bountyPoolAddress).distributeReward(
                submission.taskId,
                submission.worker,
                submission.bountyAmount
            );
            emit RewardDistributed(submissionId, submission.worker, submission.bountyAmount);
        }

        // Distribute verification rewards
        _distributeVerifierRewards(submissionId);
    }

    /**
     * @notice Distribute rewards to verifiers
     * @param submissionId The ID of the submission
     */
    function _distributeVerifierRewards(uint256 submissionId) private {
        Submission storage submission = submissions[submissionId];
        address[] memory verifiers = submissionVerifiers[submissionId];

        uint256 totalReward = 0;
        bool consensusApproved = submission.status == VerificationStatus.VERIFIED;

        for (uint256 i = 0; i < verifiers.length; i++) {
            address verifier = verifiers[i];
            Vote storage vote = verificationVotes[submissionId][verifier];

            if (!vote.hasVoted) continue;

            // Reward verifiers who voted with consensus
            bool votedWithConsensus = vote.approved == consensusApproved;

            if (votedWithConsensus) {
                // Return stake + 10% reward
                uint256 reward = vote.stake + (vote.stake * VERIFICATION_REWARD_PERCENTAGE / BASIS_POINTS);
                totalReward += reward;

                (bool success, ) = payable(verifier).call{value: reward}("");
                require(success, "Verifier reward transfer failed");

                // Update verifier reputation
                if (reputationContractAddress != address(0)) {
                    IReputationContract(reputationContractAddress).updateVerifierReputation(
                        verifier,
                        true
                    );
                }
            } else {
                // Forfeit stake for incorrect vote
                // Stake remains in contract as penalty

                // Update verifier reputation
                if (reputationContractAddress != address(0)) {
                    IReputationContract(reputationContractAddress).updateVerifierReputation(
                        verifier,
                        false
                    );
                }
            }
        }

        emit VerificationRewardsDistributed(submissionId, totalReward);
    }

    /**
     * @notice Get submission details
     * @param submissionId The ID of the submission
     * @return submission The submission details
     */
    function getSubmission(uint256 submissionId) external view returns (Submission memory) {
        return submissions[submissionId];
    }

    /**
     * @notice Get verifiers for a submission
     * @param submissionId The ID of the submission
     * @return verifiers Array of verifier addresses
     */
    function getSubmissionVerifiers(uint256 submissionId) external view returns (address[] memory) {
        return submissionVerifiers[submissionId];
    }

    /**
     * @notice Get vote details for a verifier
     * @param submissionId The ID of the submission
     * @param verifier Address of the verifier
     * @return vote The vote details
     */
    function getVote(uint256 submissionId, address verifier) external view returns (Vote memory) {
        return verificationVotes[submissionId][verifier];
    }

    /**
     * @notice Check if consensus has been reached
     * @param submissionId The ID of the submission
     * @return reached True if consensus reached
     */
    function hasConsensus(uint256 submissionId) external view returns (bool) {
        Submission memory submission = submissions[submissionId];
        return submission.status == VerificationStatus.VERIFIED || 
               submission.status == VerificationStatus.REJECTED;
    }

    /**
     * @notice Receive function to accept ETH
     */
    receive() external payable {}
}

